import React from 'react';
import { motion } from 'framer-motion';

const LoadingPage = () => {
    return (
      <motion.div
        initial={{ filter: 'blur(20px)' }} // flou initial
        animate={{ filter: 'blur(0px)' }} // pas de flou après l'animation
        transition={{ duration: 3 }} // durée de l'animation en secondes
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          //backgroundColor: 'white', // ajustez la couleur de fond selon vos besoins
          zIndex: 9999, // assurez-vous que cela s'affiche au-dessus de tout le reste
        }}
      />
    );
  };

  export default LoadingPage;
