import React from 'react';
import Path from './Menu/Path';
import Loading from './Menu/LoadingScreen'
import { BrowserRouter } from 'react-router-dom';
//import { Routes } from 'react-router-dom';


const App = () => {
  return (
    <div className="App">
      <Loading/>
      <motion.div
        initial={{ opacity: 0 }} // Opacité initiale à 0 pour ajouter un effet de fondu
        animate={{ opacity: 1 }} // Opacité à 1 pour afficher le contenu après l'animation
        transition={{ duration: 1 }} // Durée de l'animation en secondes
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          zIndex: 9998, // Assurez-vous que cela s'affiche en dessous du composant de flou
          // Ajoutez d'autres styles selon vos besoins
        }}
      ></motion.div>
      <Path />
    </div>
  );
};

export default App;
