import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
//import "../styles.css"; // Assurez-vous d'avoir importé vos styles Tailwind CSS

const WelcomeAnimation = () => {
  const [showAnimation, setShowAnimation] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowAnimation(false);
    }, 3000); // Durée de l'animation en millisecondes (3 secondes)

    return () => clearTimeout(timer);
  }, []);

  return (
    <AnimatePresence>
      {showAnimation && (
        <motion.div
          initial={{ filter: "blur(20px)", scale: 0 }}
          animate={{ filter: "blur(0px)", scale: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-gray-900 w-full h-full flex justify-center items-center text-white z-50"
        >
          <motion.h1
                initial={{ opacity: 0, scale: 1.5 }} // Redimensionnez le texte à 1.5 fois sa taille normale
                animate={{ opacity: 1, scale: 1 }} // Animation vers la taille normale
                exit={{ opacity: 0 }}
                transition={{ duration: 1 }}
                >
                Bienvenue
                </motion.h1>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default WelcomeAnimation;
