const WelcomeAnimation = () => {
  const controls = useAnimation();

  React.useEffect(() => {
    controls.start({
      scale: [1, 1.5, 1.5, 1],
      opacity: [1, 1, 0, 0],
      transition: {
        duration: 3,
        ease: "easeInOut",
      },
    });
  }, [controls]);

  return (
    <motion.div
      className="welcome-container"
      animate={controls}
      style={{ pointerEvents: "none" }}
    >
      <motion.h1 className="welcome-text">Bienvenue</motion.h1>
    </motion.div>
  );
};

export default WelcomeAnimation;
