import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const LoadingPage = () => {
//   const [isLoading, setIsLoading] = useState(true);

  // Fonction pour gérer la fin de l'animation
  const handleAnimationComplete = () => {
    setIsLoading(false);
  };

  return (
    <AnimatePresence>
      {/* {isLoading && ( */}
        <>
          <motion.div
            initial={{ opacity: 1 }} // Opacité initiale à 0 pour ajouter un effet de fondu
            animate={{ opacity: 1 }} // Opacité à 1 pour afficher le contenu après l'animation
            exit={{ opacity: 0 }} // Opacité à 0 lors de la sortie pour la faire disparaître
            transition={{ duration: 4 }} // Durée de l'animation en secondes
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              zIndex: 9998, // Assurez-vous que cela s'affiche en dessous du composant de flou
              background: 'linear-gradient(to bottom right, #1A2E3B, #14242D)', // Dégradé de couleur
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              color: 'white',
              fontSize: '3em', // Taille de la police agrandie
              fontFamily: 'Fredoka One', // Police de caractères "Fredoka One"
              fontWeight: 'bold',
              textShadow: '2px 2px 4px rgba(0,0,0,0.5)', // Ombre portée pour améliorer la lisibilité
            }}
            onAnimationComplete={handleAnimationComplete} // Gère la fin de l'animation
          >
            Bienvenue
          </motion.div>
        </>
      {/* )} */}
    </AnimatePresence>
  );
};

export default LoadingPage;
