import React from 'react';
import { motion, useAnimation } from 'framer-motion';
import '../App.css';

const WelcomeAnimation = () => {
  const controls = useAnimation();

  React.useEffect(() => {
    controls.start({
      scale: [3, 3.5, 3.5, 3],
      opacity: [1, 1, 1, 1],
      transition: {
        duration: 6,
        ease: "easeInOut",
      },
    });
  }, [controls]);

  return (
    <motion.div
      className="welcome-container"
      animate={controls}
      style={{ pointerEvents: "none" }}
    >
      <motion.h1 className="welcome-text" z-in>Bienvenue</motion.h1>
    </motion.div>
  );
};

export default WelcomeAnimation;
