import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const LoadingPage = () => {
  const [isLoading, setIsLoading] = useState(true);

  // Fonction pour gérer la fin de l'animation
  const handleAnimationComplete = () => {
    setIsLoading(false);
  };

  return (
    <AnimatePresence>
      {isLoading && (
        <motion.div
          initial={{ filter: 'blur(20px)' }} // flou initial
          animate={{ filter: 'blur(0px)' }} // pas de flou après l'animation
          exit={{ filter: 'blur(20px)' }} // flou lors de la sortie
          transition={{ duration: 3 }} // durée de l'animation en secondes
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            //backgroundColor: 'white', // ajustez la couleur de fond selon vos besoins
            zIndex: 9999, // assurez-vous que cela s'affiche au-dessus de tout le reste
          }}
          onAnimationComplete={handleAnimationComplete} // Gère la fin de l'animation
        />
      )}
    </AnimatePresence>
  );
};

export default LoadingPage;
