import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
//import BlurBackground from './BlurBackground'; // Importez votre composant de flou d'arrière-plan

const LoadingPage = () => {
  const [isLoading, setIsLoading] = useState(true);

  // Fonction pour gérer la fin de l'animation
  const handleAnimationComplete = () => {
    setIsLoading(false);
  };

  return (
    <AnimatePresence>
      {isLoading && (
        <>
          //<BlurBackground /> {/* Ajoutez le composant de flou d'arrière-plan */}
          <motion.div
            initial={{ opacity: 0 }} // Opacité initiale à 0 pour ajouter un effet de fondu
            animate={{ opacity: 1 }} // Opacité à 1 pour afficher le contenu après l'animation
            exit={{ opacity: 0 }} // Opacité à 0 lors de la sortie pour la faire disparaître
            transition={{ duration: 6 }} // Durée de l'animation en secondes
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              zIndex: 9998, // Assurez-vous que cela s'affiche en dessous du composant de flou
              // Ajoutez d'autres styles selon vos besoins
            }}
            onAnimationComplete={handleAnimationComplete} // Gère la fin de l'animation
          >
            {/* Votre contenu ici */}
          </motion.div>
        </>
      )}
    </AnimatePresence>
  );
};

export default LoadingPage;
