import { Fragment } from "react";
import { motion, useInView } from 'framer-motion'
import { useRef } from 'react';



  export default function Parcours() {
    const events = [
        {
            heading: "Début Epitech",
            subHeading: "2020",
            direction: "right"
        },
        {
            heading: "Stage",
            subHeading: "2021",
            direction: "left"
        },
        {
            heading: "Stage",
            subHeading: "2023",
            direction: "right"
        },
        {
            heading: "Début Erasmus",
            subHeading: "2023",
            direction: "left"
        },
        {
            heading: "Fin Erasmus",
            subHeading: "2024",
            direction: "right"
        },
        {
            heading: "Stage",
            subHeading: "2025",
            direction: "left"
        },
        {
            heading: "Fin Epitech",
            subHeading: "2025",
            direction: "right"
        }
    ]
    return (
        <div className="flex flex-col gap-y-3 w-full my-4">
            <Circle/>
            {events.map((event, key) => (
  <Fragment key={key}>
                    <div className="grid grid-cols-[1fr_auto_1fr] gap-x-2 items-center mx-auto">
                        {
                            event.direction === 'left' ? (
                                <EventCard heading={event.heading} subHeading={event.subHeading} direction={event.direction}/>
                            ) : (<div></div>)
                        }
                        <Pillar/>
                        {
                            event.direction === 'right' ? (
                                <EventCard heading={event.heading} subHeading={event.subHeading} direction={event.direction}/>
                            ) : (<div></div>)
                        }

                    </div>
                    { key < (events.length - 1) && <Circle/>}
                </Fragment>
            ))}
            <Circle/>
        </div>
    );
  }

  const Circle = () => {
    return (
        <div className="bg-gradient-to-r from-gray-600 to-teal-500 rounded-full w-4 h-4 mx-auto"></div>
    )
  } 
  const Pillar = () => {
    return (
        <div className="bg-gradient-to-b from-gray-600 to-teal-500 rounded-t-full rounded-b-full w-2 h-28 mx-auto"></div>
    )
  } 

const bounceEasing = [1.61, -1.04, 1.2, 1.99];

const EventCard = ({ heading, subHeading, direction }) => {
  const ref = useRef(null);
  const isInView = useInView(ref, { threshold: 0.5 });

  const variants = {
    hidden: {
      x: direction === 'right' ? -100 : 100,
      opacity: 0,
    },
    visible: {
      x: 0,
      opacity: 1,
      transition: { duration: 0.8, ease: bounceEasing },
    },
  };

  return (
    <motion.div
      ref={ref}
      initial="hidden"
      animate={isInView ? "visible" : "hidden"}
      variants={variants}
      className="transition duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-2xl flex flex-col gap-y-2 border shadow-md rounded-xl p-4"
    >
      <motion.div
        initial="hidden"
        animate={isInView ? "visible" : "hidden"}
        variants={variants}
        className="flex flex-col gap-y-2"
      >
        <div className="text-blue-800 font-bold text-lg border-b">{heading}</div>
        <div className="test-sm text-gray-700">{subHeading}</div>
      </motion.div>
    </motion.div>
  );
};
